package Java;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// For loop:
		
		for (int i = 1; i <= 10; i ++) {
			System.out.println(i);
	}
		for (int row = 1; row <= 10; row ++) {

			for (int i = 1; i <= 10; i ++)
			{
				System.out.print(i);
			}
			System.out.println();
			
			
			//for (int column = 1; column <=10; column ++) {
				//System.out.print(1);
			}
		}
			
	}

//}
